record<pre><code>-- record declaration
type reg_type is record
    irq  : std_logic;
    pend : std_logic_vector(0 to 7);
    mask : std_logic_vector(0 to 7);
end record;

-- signal of declared record
signal r, rin : reg_type;
</code></pre>

m_record<pre><code>type value_type is record
    holdtime     : time;
    filename     : string(1 to 100);
    value        : integer;
end record;

type command_type is record
    action : std_logic_vector(1 downto 0);
    value  : value_type;
    busy   : std_logic;
end record;

constant init_value_type   : value_type   := (1 ps, (others =&gt; ' '), 0);
constant init_command_type : command_type := ((others =&gt; '0'), init_value_type, '0');
</code></pre>

type std_vector<pre><code>type  std_ulogic_vector is array (natural range &lt;&gt;) of std_ulogic;
type  std_logic_vector  is array (natural range &lt;&gt;) of std_logic;

std_logic_vector_sig &lt;= "11"; --  3
