</code></pre>

if<pre><code>if s0 = '0' and s1 = '0' then
    output &lt;= in0;
elsif s0 = '1' and s1 = '0' then
    output &lt;= in1;
elsif s0 = '0' and s1 = '1' then
    output &lt;= in2;
elsif s0 = '1' and s1 = '1' then
    output &lt;= in3;
else
    output &lt;= 'X';
end if;
</code></pre>

case<pre><code>case sel is
  when  "00"  =&gt;  output &lt;= in0;
  when  "01"  =&gt;  output &lt;= in1;
  when  "10"  =&gt;  output &lt;= in2;
  when  "11"  =&gt;  output &lt;= in3;
  when others =&gt;  output &lt;= 'X';
end case;
</code></pre>

for loop<pre><code>for i in 1 to 8 loop
  output(i) &lt;= input(i + 8);
end loop;

for i in input'range loop
  output(i) &lt;= input(i);
end loop;
</code></pre>

clock<pre><code>constant sys_CLK_freq   : real := 150.00E6;
constant sys_CLK_period : time := (1.0/sys_CLK_freq) * 1 sec;

signal sys_CLK_int      : std_logic;
signal sys_CLK          : std_logic;
----------------------------
-- Only signal
sys_CLK_int &lt;= not sys_CLK_int after sys_CLK_period / 2;
sys_CLK     &lt;= transport sys_CLK_int after sys_CLK_period * 9 / 10;

-- In a process
process
begin
    sys_CLK_int    &lt;= '0';   wait for sys_CLK_period / 2;
    sys_CLK_int    &lt;= '1';   wait for sys_CLK_period / 2;
end process;
sys_CLK     &lt;= transport sys_CLK_int after sys_CLK_period * 9 / 10;
</code></pre>

